package com.tyfone.csc;

import java.util.ArrayList;
import java.util.List;

import com.tyfone.csc.helper.ConversionHelper;
import com.tyfone.csc.helper.ErrorCodes;
import com.tyfone.csc.smartcard.Applet;
import com.tyfone.csc.smartcard.CommandAPDU;
import com.tyfone.csc.smartcard.ResponseAPDU;
import com.tyfone.csc.smartcard.Smartcard;

/**
 * @author Srikar created on @date Sep 9, 2014
 */
public class ExtendedLengthApplet extends Applet {

	private List<CommandAPDU> commandAPDUList;

	public ExtendedLengthApplet(Smartcard smartCard, String appletID) {
		super(smartCard, appletID);
		commandAPDUList = new ArrayList<CommandAPDU>();
		fillCommandAPDUList();
	}

	/**
	 * 
	 */
	private static final long serialVersionUID = 1L;

	private void fillCommandAPDUList() {
		byte[] apduData = ConversionHelper
				.hexStringToByteArray("B025000000012C");
		CommandAPDU commandAPDU = new CommandAPDU(apduData);
		commandAPDUList.add(commandAPDU);

		byte[] apduData3000 = ConversionHelper.hexStringToByteArray(apdu3000);
		CommandAPDU commandAPDU3000 = new CommandAPDU(apduData3000);
		commandAPDUList.add(commandAPDU3000);
		
		byte[] apduData2000 = ConversionHelper.hexStringToByteArray(apdu2000);
		CommandAPDU commandAPDU2000 = new CommandAPDU(apduData2000);
		commandAPDUList.add(commandAPDU2000);
		
		byte[] apduData1000 = ConversionHelper.hexStringToByteArray(apdu1000);
		CommandAPDU commandAPDU1000 = new CommandAPDU(apduData1000);
		commandAPDUList.add(commandAPDU1000);
		
		byte[] apduData500 = ConversionHelper.hexStringToByteArray(apdu500);
		CommandAPDU commandAPDU500 = new CommandAPDU(apduData500);
		commandAPDUList.add(commandAPDU500);
		
		byte[] apduData300 = ConversionHelper.hexStringToByteArray(apdu300);
		CommandAPDU commandAPDU300 = new CommandAPDU(apduData300);
		commandAPDUList.add(commandAPDU300);

	}

	String apdu3000 = "";
	String apdu2000 = "";
	String apdu1000 = "";
	String apdu500 = "";
	String apdu300 = "";

	@Override
	public String toString() {
		// TODO Auto-generated method stub
		return "Extended length";
	}

	public List<CommandAPDU> getAPDUList() {
		return commandAPDUList;
	}

	@Override
	public void onReceiveResponse(byte[] response, ErrorCodes error) {

	}

	@Override
	public void onReceiveResponse(ResponseAPDU response, Applet applet, ErrorCodes error) {

	}
}
